import React, { useState } from 'react'
import 'bootstrap/dist/css/bootstrap.min.css'
import Link from 'next/link'
import { Button, Card, Row, Col } from 'react-bootstrap'

const Home = () => {
    const [anime, setTopAnimes] = useState()
    const [topMangas, setTopMangas] = useState([])

    const getRandomInt = (max) => {
        return Math.floor(Math.random() * Math.floor(max))
    }

    const findRandomAnime = async () => {
        let random = await getRandomInt(50)
        let res = await fetch(`https://api.jikan.moe/v3/top/anime/${random}/tv`)
        let data = await res.json()
        setTopAnimes({ title: data.top[random].title, score: data.top[random].score, image: data.top[random].image_url})
    }

    const findRandomManga = async () => {
        let random = await getRandomInt(50)
        let res = await fetch(`https://api.jikan.moe/v3/top/manga/${random}/manga`)
        let data = await res.json()

        setTopMangas([])

        for (let i = 0; i < data.top.length; i++) {
            setTopMangas([...topMangas, { title: data.top[i].title, score: data.top[i].score, id: topMangas.length, image: data.top[i].image_url}])
        }
        console.log(data)
    }

    return (
        <div className='ml-2'>
            <Row>
                <Col>
                    <h1 className='text-primary'>Sora</h1>
                    <Link href='/Wen'>My Media List</Link>
                </Col>
                <Col>
                    <Button onClick={() => findRandomAnime()}>Get Random Anime</Button>
                    <Button onClick={() => findRandomManga()}>Get Random Manga</Button>
                    <Card>
                            <Card.Body>
                                <Card.Title>{anime} | {anime}</Card.Title>
                                <Card.Img src={anime} className='col-md-2'></Card.Img>
                            </Card.Body>
                        </Card>

                    {/* {topAnimes.map(anime => (
                        <Card key={anime.id}>
                            <Card.Body>
                                <Card.Title>{anime.title} | {anime.score}</Card.Title>
                                <Card.Img src={anime.image} className='col-md-2'></Card.Img>
                            </Card.Body>
                        </Card>
                    ))} */}
                    {topMangas.map(manga => (
                        <Card key={manga.id}>
                            <Card.Body>
                                <Card.Title>{manga.title} | {manga.score}</Card.Title>
                                <Card.Img src={manga.image} className='col-md-2'></Card.Img>
                            </Card.Body>
                        </Card>
                    ))}
                </Col>
            </Row>
        </div>
)}

export default Home
