import React, { useState } from 'react'
import 'bootstrap/dist/css/bootstrap.min.css'
import Link from 'next/link'
import { Button, Card, Row, Col } from 'react-bootstrap'

const Home = () => {
    const [topAnimes, setTopAnimes] = useState([])

    const findTop = async () => {
        let res = await fetch('https://api.jikan.moe/v3/top/anime/1')
        let data = await res.json()

        for (let i = 0; i < data.top.length; i++) {
            setTopAnimes([...topAnimes, { title: data.top[i].title, score: data.top[i].score, id: topAnimes.length, image: data.top[i].image_url}])
        }
        console.log(data)
    }

    return (
        <div className='ml-2'>
            <Row>
                <Col>
                    <h1 className='text-primary'>Sora</h1>
                    <Link href='/Wen'>My Media List</Link>
                </Col>
                <Col>
                    <Button onClick={() => findTop()}>Get Random Anime</Button>
                    {topAnimes.map(anime => (
                        <Card key={anime.id}>
                            <Card.Body>
                                <Card.Title>{anime.title} | {anime.score}</Card.Title>
                                <Card.Img src={anime.image} className='col-md-2'></Card.Img>
                            </Card.Body>
                        </Card>
                    ))}
                </Col>
            </Row>
        </div>
)}

export default Home
